mapper.select:
select b.*, ui.uname, d.did, d.dname from userinfo ui join dept d on ui.did = d.did join tboard b on b.uemail = ui.uemail
and b.code = #{code}
order by b.tmpregdate desc

mapper.insert:
INSERT INTO board (bno, title, bcontent, uemail, regdate, caid) SELECT board_seq.nextval, A.title, A.tmpcontent, A.uemail, A.systimestamp, A.caid FROM (
SELECT #{item.tmptitle} as title, #{item.tmpcontent} as tmpcontent, #{item.uemail} as uemail, current_timestamp as systimestamp, #{item.caid} as caid FROM DUAL
) A
